import { onMount } from 'svelte'

let items
let wrapperHeight

onMount(async() => {
  [...items.children].forEach(sample => {
    let wrapper = sample.querySelector('.wrapper')
    let total = sample.querySelectorAll('img').length
    wrapperHeight = sample.children[0].offsetHeight
    wrapper.style = `--wrapper-width:${sample.children[0].offsetWidth * total}px`
	wrapper.dataset.index = 0
    setInterval(function(){
      let i = parseInt(wrapper.dataset.index)
	  if(i + 1 < wrapper.querySelectorAll('img').length) {
		wrapper.dataset.index = i + 1
	  } else {
		wrapper.dataset.index = 0
	  }
      wrapper.style = `--wrapper-width:${sample.children[0].offsetWidth * total}px;--wrapper-left:-${wrapper.dataset.index * sample.children[0].offsetWidth}px;`
    }, 2000)
  })
})

