import { onMount } from 'svelte'
import Icon from "@iconify/svelte/dist/Icon.svelte"

let activeSlide = 0
let wrapper, vw=1920, vh=900
let cp = ''
let mobileMenu

onMount(async() => {
  vw = window.innerWidth
  vh = window.innerHeight
  setInterval(() => {
    wrapper.children[activeSlide].classList.remove('active')
    activeSlide = wrapper.children.length > activeSlide + 1 ? activeSlide + 1 : 0
    wrapper.children[activeSlide].classList.add('active')
  }, 5000)
  let y = new Date().getFullYear()
  if(y > 2022) cp = `-${y}`
  window.onresize = () => {
    vw = window.innerWidth
    vh = window.innerHeight
  }
})
